import i18next from "./ecui-i18n";
import { I18nextProvider } from "react-i18next";
export const useComponentsTranslation = () => {
    const updatei18next = lang => {
        const langPromise = new Promise((resolve, reject) => {
            if (!lang) {
                resolve("done");
            }
            if (lang && !i18next.hasResourceBundle(lang, "components")) {
                import("../locales/" + lang + "/components.json").then(response => {
                    i18next.addResourceBundle(lang, "components", response.default);
                    i18next.changeLanguage(lang);
                }, err => console.error("Translation file for " + lang + " not found", err));
                resolve("done");
            }
            else if (lang !== i18next.language) {
                i18next.changeLanguage(lang);
                resolve("done");
            }
        });
        return langPromise;
    };
    return { i18next, I18nextProvider, updatei18next };
};
