import { useEffect, useState } from "react";
import Service from "./Service";

export const LOADED = "LOADED";
export const LOADING = "LOADING";
export const ERROR = "ERROR";

export default class SearchService extends Service {
	constructor(url) {
		super("search", url);
	}

	searchRepo(repoId, body, { thumb = 1, media } = {}) {
		return this.post(["_esko", "search", "nodes", "repo", repoId, "_search"], body, {
			"Content-Type": "application/json",
			"EskoCloud-Search-Flags": JSON.stringify({ thumb, media })
		});
	}

	useSearch(repoId, query, { aggs, sort, page = 1, size = 20, path, thumb = 1, media, infinite, ...options } = {}) {
		if (!repoId && query) {
			throw "query and repoId should be defined";
		}
		const [data, setData] = useState({
			results: [],
			status: LOADING,
			total: 0,
			addResults: (...res) => {
				setData(({ results, total, ...data }) => ({ ...data, total: total + res.length, results: [...res, ...results] }));
			},
			removeResults: (...nodeIds) => {
				setData(({ results, total, ...data }) => ({
					...data,
					total: total - nodeIds.length,
					results: results.filter(({ _source }) => nodeIds.indexOf(_source.nodeId) === -1)
				}));
			},
			mutateResult: source => {
				setData(({ results, ...data }) => ({
					...data,
					results: results.map(r => {
						const { _source } = r;
						if (_source.nodeId !== source.nodeId) {
							return r;
						} else {
							return { ...r, _source: source };
						}
					})
				}));
			}
		});

		useEffect(() => {
			// If path is suppplied, we use the join engine to first check for the parentNodeId
			let canceled = false;
			setData(state => ({ ...state, status: LOADING }));
			const stages = path
				? [
						{
							index: {
								indexType: "nodes",
								repoId
							},
							stageType: "select",
							query: {
								size: 1,
								query: {
									term: { path }
								}
							}
						},
						{
							index: {
								indexType: "nodes",
								repoId
							},
							stageType: "select",
							query: {
								size,
								from: (page - 1) * size,
								sort,
								aggs,
								query: {
									bool: {
										filter: {
											term: { parentNodeId: "{{0:/hits/hits/0/_source/nodeId}}" }
										},
										must: {
											...query
										}
									}
								}
							}
						}
				  ]
				: // If no path supplied, just use the join search engine to do the search
				  [
						{
							index: {
								indexType: "nodes",
								repoId
							},
							stageType: "select",
							query: {
								size,
								from: (page - 1) * size,
								aggs,
								query,
								sort
							}
						}
				  ];
			this.post(
				"_esko/join",
				{ stages },
				{
					"Content-Type": "application/json",
					"EskoCloud-Search-Flags": JSON.stringify({ thumb: thumb ? 1 : undefined, media: media ? 1 : undefined })
				}
			)
				.then(res => {
					if (!canceled) {
						setData(data => {
							const newResults = res?.hits?.hits || [];
							const results = infinite && page > 1 ? data.results.concat(newResults) : newResults;
							const aggregations = { ...(res?.aggregations || {}) };
							return { ...data, total: res?.hits?.total || 0, results, status: LOADED, aggregations };
						});
					}
				})
				.catch(resp => {
					if (!canceled) {
						setData(data => ({
							...data,
							status: ERROR,
							error: resp.status
						}));
					}
				});
			return () => {
				canceled = true;
			};
		}, [media, page, path, query, repoId, size, sort, thumb, aggs, infinite]);

		return data;
	}
}
